import React from "react";

export default function About() {
  return (
    <section id="about" className="py-20 px-5 bg-gradient-to-br from-blue-50 to-indigo-100 dark:from-gray-800 dark:to-gray-900 text-gray-900 dark:text-white">
      <div className="max-w-4xl mx-auto text-center">
        <h2 className="text-4xl font-bold mb-8 text-transparent bg-clip-text bg-gradient-to-r from-blue-600 to-purple-600 dark:from-blue-400 dark:to-purple-400">
          About Me
        </h2>
        <div className="w-32 h-32 bg-gradient-to-br from-blue-400 to-purple-500 rounded-full flex items-center justify-center text-4xl font-bold mb-8 mx-auto shadow-lg hover:scale-105 transition-transform duration-300">
          K
        </div>
        <p className="text-xl leading-relaxed max-w-2xl mx-auto">
          Hi, I'm <strong className="text-blue-600 dark:text-blue-400">Kashif</strong>. I'm a passionate Full Stack Developer who
          loves building web applications and exploring new technologies. I enjoy creating innovative solutions and continuously learning in the ever-evolving tech landscape.
        </p>
      </div>
    </section>
  );
}
